INSERT INTO `eq_add_list`     (eq_addno , ask_eno, sign_eno, ask_date, eq_sort, eq_name,mk_no)    SELECT     nextSeqVal('seq_eq_addno')        ,?        ,e.e_no         ,DATE_FORMAT(now(), '%Y-%m-%d')        ,IF( EXISTS(SELECT eq_sort FROM `eq_sort`                    WHERE eq_sort = ? )           , ?           , NULL )            ,CASE WHEN (SELECT eq_name FROM `eq_add_list`                    WHERE eq_name = ?                       AND outcome IN ('eqa-1','eqa-3')                   ) IS NULL              THEN ?              ELSE NULL           END       ,?      FROM      `emp` e       INNER JOIN `dept` d       ON e.d_no = d.d_no     WHERE      e.au_no = 35       AND e.d_no = 9001      ORDER BY      e.e_rank DESC     ,e.e_no ASC     LIMIT 1    ;